import { DiaryEntry, NewDiaryEntry, Visibility, Weather } from "./types"

import { useEffect, useState } from "react";
import { createDiary, getAllDiaries } from "./diariayServices";

const App = () => {
  const [diaries, setDiaries] = useState<DiaryEntry[]>([]);
  const [newData, setNewData] = useState<string>('');
  const [newVisibility, setNewVisibility] = useState<Visibility>(Visibility.Great);
  const [newWeather, setNewWeather] = useState<Weather>(Weather.Sunny);
  const [newComment, setNewComment] = useState<string>('');
  useEffect(() => {
    getAllDiaries().then(data => {
      setDiaries(data as DiaryEntry[])
    })
  }, [])

  const diaryCreation = async (event: React.SyntheticEvent) => {
    event.preventDefault()
    const newDiary: NewDiaryEntry = {
      date: newData,
      weather: newWeather,
      visibility: newVisibility,
      comment: newComment
    }
    await createDiary(newDiary);

    setNewData('')
    setNewVisibility(Visibility.Great)
    setNewWeather(Weather.Sunny)
    setNewComment('')
  };

  return (
    <div>
      <h1>Creat Diary</h1>
      <form onSubmit={diaryCreation}>
        data: <input
          value={newData}
          onChange={(event) => setNewData(event.target.value)}
        />
        weather: <input
          value={newWeather}
          onChange={(event) => setNewWeather(event.target.value as Weather)}
        />
        visibility: <input
          value={newVisibility}
          onChange={(event) => setNewVisibility(event.target.value as Visibility)}
        />
        comment: <input
          value={newComment}
          onChange={(event) => setNewComment(event.target.value)}
        />
        <button type='submit'>add</button>
      </form>

      <h1>Diary entries</h1>
      <ul>
        {diaries.map(diaray =>
          <li key={diaray.id}>
            <h3>{diaray.date}</h3>
            <br/>
            <p>visibility: {diaray.visibility}</p>
            <p>weather: {diaray.weather}</p>
          </li>
        )}
      </ul>
    </div>
  )
}

export default App;
